d(){	var oldfields = toit_fields_count++;			for(i=1; i<=oldfields; i++){		var id= 'toit_order'+i;		jQuery('select[name="'+id+'"]').append('<option value="'+toit_fields_count+'" >'+toit_fields_count+'</option>');	}	$html = '<tr><td><input type="text" name="toit_label'+toit_fields_count+'" value="" /></td><td><select name="toit_field'+toit_fields_count+'"><option value="textbox">TextBox</option><option value="textarea">TextArea</option><option value="email">Email</option><option value="url">Url</option><option value="checkbox">CheckBox</option></select></td><td><input type="checkbox" name="toit_required_'+toit_fields_count+'" /></td><td><select name="toit_order'+toit_fields_count+'">';	for(i=1; i<=toit_fields_count; i++){		if(i==toit_fields_count) var selected = 'selected="selected"';		else var selected = '';		$html += '<option value="'+i+'" '+selected+'>'+i+'</option>';	}	$html += '</select></td></tr>';		jQuery("#toit-form-table").append($html);		jQuery("#toit_fields_count").val(toit_fields_count);	}</script>